-- V8: Enhanced Panchayat Website Templates with Comprehensive Sections
-- This migration creates 9 professional templates (3 per language) with 12-14 sections each
-- Includes: Hero, Services, Statistics, News, Gallery, Social Media, and more
-- Each template uses image placeholders and professional color themes

-- Ensure is_page_template column exists (in case V5-V7 haven't run)
DO $$ 
BEGIN
    IF NOT EXISTS (
        SELECT 1 FROM information_schema.columns 
        WHERE table_name = 'section_templates' 
        AND column_name = 'is_page_template'
    ) THEN
        ALTER TABLE section_templates 
        ADD COLUMN is_page_template BOOLEAN NOT NULL DEFAULT false;
    END IF;
END $$;

-- Delete old templates to ensure clean slate
DELETE FROM section_templates 
WHERE (name LIKE '%Professional Government Services%' 
   OR name LIKE '%Modern Community Portal%' 
   OR name LIKE '%Elegant Public Services%'
   OR name LIKE '%पेशेवर सरकारी सेवाएं%'
   OR name LIKE '%व्यावसायिक सरकारी सेवा%'
   OR name LIKE '%आधुनिक सामुदायिक पोर्टल%'
   OR name LIKE '%आधुनिक समुदाय पोर्टल%'
   OR name LIKE '%सुरुचिपूर्ण सार्वजनिक सेवा%'
   OR name LIKE '%सुरुचिपूर्ण सार्वजनिक सेवाएं%')
  AND is_page_template = true;

